import React from 'react';
import fetch from 'isomorphic-unfetch';
import s from './login.module.scss';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { useRouter } from 'next/router';
import { authenticationService } from '../../_services';

export default function SignIn() {
  const router = useRouter();

  return (
    <div className={s.login_page_form}>
      <div className="alert alert-info">
        <strong>Normal User</strong> - U: user P: user<br />
        <strong>Administrator</strong> - U: admin P: admin<br />
        <strong>Provider</strong> - U: pro P: pro
        </div>
      <h2>Login</h2>
      <Formik
        initialValues={{
          username: '',
          password: ''
        }}
        validationSchema={Yup.object().shape({
          username: Yup.string().required('Username is required'),
          password: Yup.string().required('Password is required')
        })}
        onSubmit={({ username, password }, { setStatus, setSubmitting }) => {
          setStatus();
          // authenticationService.login(username, password)
          fetch(`https://intim-vibe-api.padilo.pro/api/login`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ email: 'user@com', password: 'user' })
          })
          // .then(handleResponse)
            .then(
              user => {
                console.log(user);
                
                const { from } =
                  // this.props.location.state ||
                  { from: { pathname: "/" } };
                router.push(from);
              },
              error => {
                setSubmitting(false);
                setStatus(error);
              }
            );
        }}
        render={({ errors, status, touched, isSubmitting }) => (
          <Form>
            <div className="form-group">
              <label htmlFor="username">Username</label>
              <Field name="username" type="text" className={'form-control' + (errors.username && touched.username ? ' is-invalid' : '')} />
              <ErrorMessage name="username" component="div" className="invalid-feedback" />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <Field name="password" type="password" className={'form-control' + (errors.password && touched.password ? ' is-invalid' : '')} />
              <ErrorMessage name="password" component="div" className="invalid-feedback" />
            </div>
            <div className="form-group">
              <button type="submit" className={`btn ${s.submit_btn}`} disabled={isSubmitting}>Submit</button>
              {isSubmitting &&
                <div className="spinner-border spinner-border-sm text-primary ml-3" role="status">
                  <span className="sr-only">Loading...</span>
                </div>
              }
            </div>
            {status &&
              <div className={'alert alert-danger'}>{status}</div>
            }
          </Form>
        )}
      />
    </div>
  )
}
