import React, { useState, useEffect } from "react";
import s from "./profile.module.scss";
// import Slider from "react-slick";

export default function ProfileCarousel({ photo }) {
  // const [photos, setPhotos] = useState([]);
  // const [visiblePhoto, setVisiblePhoto] = useState('');
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    // setPhotos(photo);
    setCurrentIndex(0);
    // setVisiblePhoto(photo[currentIndex]);
  }, []);

  const changePhotoForvard = n => {
    if (photo.length > 1) {
      if (currentIndex < photo.length - 1) {
        setCurrentIndex(currentIndex + 1);
      } else {
        setCurrentIndex(0);
      }
    }
  }
  const changePhotoBack = n => {
    if (photo.length > 1) {
      if (currentIndex > 0) {
        setCurrentIndex(currentIndex - 1);
      } else {
        setCurrentIndex(photo.length - 1);
      }
    }
  }

  // const settings = {
  //   infinite: true,
  //   speed: 500,
  //   slidesToShow: 1,
  //   slidesToScroll: 1,
  //   arrows: true,
  //   dots: false,
  //   fade: true
  // };

  return (
    <div className="w-100" style={{ background: '#1b1c1f' }}>
      {/* <Slider
        {...settings}
        style={{
          maxWidth: "1260px",
          margin: "0 auto"
        }}
      > */}
      <span onClick={changePhotoBack}>&lt;</span>

      <div className={s.slide}>
        <img src={photo[currentIndex]}
          alt="model" className="mx-auto" />
      </div>

      <span onClick={changePhotoForvard}>&gt;</span>
      {/* {photo.length > 1 ?
        photo.map((p, i) => (
          <div key={i}>
            <div className={s.slide}>
              <img src={p} alt="model" className="mx-auto" />
            </div>
          </div>
        ))
        : <div>
          <div className={s.slide}>
            <img src={photo[0]} alt="model" className="mx-auto" />
          </div>
        </div>} */}
      {/* </Slider> */}
    </div>
  );
}
