import React, { useState } from 'react'
import { adminService } from '../../_services/admin.service';
import Alert from '../test/Alert';
import { connect } from 'react-redux';
import { showAlert } from '../../redux/actions/actions';

function AdminRegistration(props) {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const [confirmedPassword, setConfirmedPassword] = useState('');

  const submitHandler = e => {
    e.preventDefault();
    if (!name.length || !password.length || !email.length || !confirmedPassword.length) {
      return props.showAlert('All fields must be filled');
    } 
    adminService.registration(name, password, email, confirmedPassword);
  }

  return (
    <div className="text-white">
      <span>New admin registration</span>

      {props.alert && <Alert text={props.alert} />}

      <form onSubmit={e => submitHandler(e)} className="form-group container">
        <label className="form-group d-block">
          name
          <input type="text" className="form-control"
            onChange={e => setName(e.target.value)} />
        </label>
        <label className="form-group d-block">
          password
          <input type="password" className="form-control"
            onChange={e => setPassword(e.target.value)} />
        </label>
        <label className="form-group d-block">
          email
          <input type="email" className="form-control"
            onChange={e => setEmail(e.target.value)} />
        </label>
        <label className="form-group d-block">
          password_confirmation
          <input type="password" className="form-control"
            onChange={e => setConfirmedPassword(e.target.value)} />
        </label>
        <button type="submit">Submit</button>
      </form>
    </div>
  )
}

const mapStateToProps = state => ({
  alert: state.app.alert
})

const mapDispatchToProps = {
  showAlert
}

export default connect(mapStateToProps, mapDispatchToProps)(AdminRegistration);
