import { TEST_CREATE_POST, SHOW_LOADER, HIDE_LOADER, SHOW_ALERT, HIDE_ALERT, TEST_REQUEST_POSTS, GET_CATEGORIES, GET_ALL_POSTS, GET_LOCAITONS_INFO, GET_CITIES, CREATE_SUPPORT_TASK, GET_ALL_USERS_TASKS } from "./types"
import fetch from 'isomorphic-unfetch';
import { authenticationService } from "../../_services";

// T E S T 
export function createPost(post: any): any {
    return {
        type: TEST_CREATE_POST,
        payload: post
    }
};
export function showLoader() {
    return {
        type: SHOW_LOADER
    }
}
export function hideLoader() {
    return {
        type: HIDE_LOADER
    }
}
export function showAlert(text: string): any {
    return (dispatch: Function) => {
        dispatch({
            type: SHOW_ALERT,
            payload: text
        })
        setTimeout(() => {
            dispatch(hideAlert())
        }, 3000)
    }
}
export function hideAlert() {
    return {
        type: HIDE_ALERT
    }
}
export function fetchPosts(): any {
    return {
        type: TEST_REQUEST_POSTS
    }
};
export const getCategories = () => async (dispatch: any) => {
    const response = await fetch('https://intim-vibe-api.padilo.pro/api/categories');
    const promise = response.json();
    promise.then(res => {
        return dispatch({ type: GET_CATEGORIES, payload: res.data });
    }).catch(err => console.error('Error: ', err));
}
export const getAllPosts = () => async (dispatch: any) => {
    const response = await fetch(`https://intim-vibe-api.padilo.pro/api/posts`);
    const promise = response.json();
    promise.then(res => {
        return dispatch({ type: GET_ALL_POSTS, payload: res.data });
    }).catch(err => console.error('Error: ', err));
}
export const getLocationsInfo = () => async (dispatch: any) => {
    const response = await fetch(`https://intim-vibe-api.padilo.pro/api/location`);
    const promise = response.json();
    promise.then(res => {
        return dispatch({ type: GET_LOCAITONS_INFO, payload: res.data });
    }).catch(err => console.error('Error: ', err));
}
export const getCities = (stateId: number | string) => async (dispatch: any) => {
    const response = await fetch(`https://intim-vibe-api.padilo.pro/api/cities/${stateId}`);
    const promise = response.json();
    promise.then(res => {
        return dispatch({ type: GET_CITIES, payload: res });
    }).catch(err => console.error('Error: ', err));
}
export const createSupportTask = (task: any) => async (dispatch: any) => {
    const user = authenticationService.currentUserValue;
    const response = await fetch(`https://intim-vibe-api.padilo.pro/api/support/create`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'Access-Control-Allow-Origin': '*',
            'Authorization': `${user.token_type} ${user.token}`
        },
        body: JSON.stringify(task)
    });
    const promise = response.json();
    promise.then(res => console.log(res))
        .catch(err => console.error('Error: ', err));
    return dispatch({ type: CREATE_SUPPORT_TASK, payload: task })
}
export const getAllUsersTasks = (userId: number | string) => async (dispatch: any) => {

    return dispatch({ type: GET_ALL_USERS_TASKS, tasks: [] });
}
