import React, { useState } from 'react'
import s from './support.module.scss'
import $ from 'jquery'

export default function Support({ tasks, createSupportTask }) {
    const [showText, setShowText] = useState(false);
    const [theme, setTheme] = useState('');
    const [text, setText] = useState('');

    const openTaskText = id => {
        !showText ?
            $(`.show-${id}`).css({
                whiteSpace: 'normal',
                overflow: 'visible',
            }) : $(`.show-${id}`).css({
                whiteSpace: 'nowrap',
                overflow: 'hidden'
            });
        setShowText(!showText);
    }

    const createTask = () => {
        if (theme.length > 2 && theme.length < 51 && text.length) {
            createSupportTask({
                subject: theme, message: text
            });
            setTheme('');
            setText('');
        }
    }

    return (
        <div className={s.support}>
            <div className={s.create_task}>
                <div className={s.task_title}>
                    CREATE NEW TASK
                </div>
                <div className={s.task_body}>
                    <input type="text" placeholder="Theme" className={`form-control`} onChange={e => setTheme(e.target.value)} />
                    <textarea className={`form-control`} rows="3" onChange={e => setText(e.target.value)} placeholder="Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Quis ipsum suspendisse ultrices gravida. Risus commodo viverra maecenas accumsan lacus vel facilisis."></textarea>
                    <div className={s.send_btn}>
                        <span onClick={createTask}>SEND</span>
                    </div>
                </div>
            </div>

            <div className={s.history}>
                <span>HISTORY</span>
            </div>

            {tasks && tasks.length ? tasks.map((task, i) => {
                return <div key={i} className={s.history_item}>
                    <div className={`${s.task_body}`}>
                        <div className={`text-white`}>{task.theme}</div>
                        <div className={`text-white ${s.text_of_support_message} show-${task.id}`} >{task.text}</div>
                        <div className={s.send_btn}>
                            <span onClick={e => openTaskText(task.id)}>{!showText ? 'READ MORE' : 'HIDE'}
                            </span>
                        </div>
                    </div>
                </div>
            }) : <div className="h1 text-info text-center py-5 ">No tasks...</div>}
        </div>
    )
}
