import React, { useState } from "react";
import Link from 'next/link';
import s from "./horizontal.module.scss";
import fav from "../../assets/images/main/signs/favorite-yel.png";
import crown from "../../assets/images/main/signs/crown-yel.png";
import star from "../../assets/images/main/signs/star-yel.png";
import plane from "../../assets/images/main/signs/plane-yel.png";

export default function LocHorCarousel({ posts, addToFavorites }) {
  const [currentIndex, setCurrentIndex] = useState(0);

  const autoSlide = delay => {
    setInterval(() => {
      changePhotoForvard();
    }, delay);
  }

  useEffect(() => {
    setCurrentIndex(0);
  }, []);

  const changePhotoForvard = () => {
    if (photo.length > 1) {
      if (currentIndex < photo.length - 1) {
        $('.model-photo').fadeOut(() => {
          setCurrentIndex(currentIndex + 1);
          setTimeout(() => {
            $('.model-photo').fadeIn();
          }, 100);
        })
      } else {
        $('.model-photo').fadeOut(() => {
          setCurrentIndex(0);
          setTimeout(() => {
            $('.model-photo').fadeIn();
          }, 100);
        })
      }
    }
  }
  const changePhotoBack = () => {
    if (photo.length > 1) {
      if (currentIndex > 0) {
        $('.model-photo').fadeOut(() => {
          setCurrentIndex(currentIndex - 1);
          setTimeout(() => {
            $('.model-photo').fadeIn();
          }, 100);
        })
      } else {
        $('.model-photo').fadeOut(() => {
          setCurrentIndex(photo.length - 1);
          setTimeout(() => {
            $('.model-photo').fadeIn();
          }, 100);
        })
      }
    }
  }

  return (
    <div className={s.loc_hor_carousel}>
      <div className={s.loc_hor_carousel_header}>
        <h5 className="text-white">Paid Announcements</h5>
      </div>


      <div className={s.photo_slider}>
        {photo.length > 1
          ? <span className={s.photo_toggler_back}
            onClick={changePhotoBack}>&lt;</span>
          : null}

        <div className={s.slide}>
          <Link href={{ pathname: "/modelProfile", query: { id: post[currentIndex].id } }}><a>
            <div className={s.loc_hor_carousel_slide}>
              <img src={post[currentIndex].photo_horizontal} alt={post[currentIndex].model_name} className="w-100 model-photo" />
              <div className={s.loc_slide_info}>
                <div className={s.loc_slide_info_head}>
                  <h5 className="text-white">{post[currentIndex].model_name} {post[currentIndex].second_name}</h5>
                  <div className={s.slide_network}>{post[currentIndex].status}</div>
                  <div className={s.slide_control}>
                    <img src={fav} alt="" onClick={(e) => {
                      e.preventDefault();
                      addToFavorites(post[currentIndex].id);
                    }} />
                    <img src={crown} alt="" />
                    <img src={star} alt="" />
                    <img src={plane} alt="" />
                  </div>
                </div>
                <div className={s.slide_location}>
                  <p className="m-0">{post[currentIndex].state.name} / {post[currentIndex].city.name}</p>
                </div>
                <div className={s.slide_verification}>
                  <span className={s.verification}>{post[currentIndex].is_verify ? 'OTHER VIP VERIFIED' : null}</span>
                  <span className={`text-white ${s.status}`}>SPECIAL STATUS</span>
                </div>
              </div>
            </div>
          </a></Link>
        </div>
        {/* <div className={s.slide}>
          <img src={photo[currentIndex]} className="model-photo" alt="model" />
        </div> */}

        {photo.length > 1
          ? <span className={s.photo_toggler_forvard}
            onClick={changePhotoForvard}>&gt;</span>
          : null}
      </div>
      {posts.map((post, i) => {
        return <div key={i}>
          <Link href={{ pathname: "/modelProfile", query: { id: post[currentIndex].id } }}><a>
            <div className={s.loc_hor_carousel_slide}>
              <img src={post[currentIndex].photo_horizontal} alt="model" className="w-100" />
              <div className={s.loc_slide_info}>
                <div className={s.loc_slide_info_head}>
                  <h5 className="text-white">{post[currentIndex].model_name} {post[currentIndex].second_name}</h5>
                  <div className={s.slide_network}>{post[currentIndex].status}</div>
                  <div className={s.slide_control}>
                    <img src={fav} alt="" onClick={(e) => {
                      e.preventDefault();
                      addToFavorites(post[currentIndex].id);
                    }} />
                    <img src={crown} alt="" />
                    <img src={star} alt="" />
                    <img src={plane} alt="" />
                  </div>
                </div>
                <div className={s.slide_location}>
                  <p className="m-0">{post[currentIndex].state.name} / {post[currentIndex].city.name}</p>
                </div>
                <div className={s.slide_verification}>
                  <span className={s.verification}>{post[currentIndex].is_verify ? 'OTHER VIP VERIFIED' : null}</span>
                  <span className={`text-white ${s.status}`}>SPECIAL STATUS</span>
                </div>
              </div>
            </div>
          </a></Link>
        </div>
      })}
    </div>
  );
}
