import React, { useState } from "react";
import s from "./cabinet.module.scss";
import $ from 'jquery';

export default function ModelCabinetControl(props) {
  const [activeBtn, setActiveBtn] = useState('ads');

  /// for (let btn of $('.cabinet_control_btn')) {
  //   if ($(btn).not($(e.target)).hasClass('cabinet_control_btn')) {
  //     $(btn).removeClass('active_btn_blue');
  //   }
  // }
  // $(e.target).addClass('active_btn_blue');
  // }

  return (
    <div className="py-4">
      <div className={s.cab_control_name}>
        <span>USERNAME</span>
        <span>EXIT</span>
      </div>
      <div className={s.cab_control_title}>
        <h5 className="text-white">BUSINESS CABINET</h5>
      </div>
      <div className={s.cab_control_buttons}>
        <span className={`${activeBtn === 'ads' ? 'active_btn_blue' : ''}`} onClick={(e) => props.setActiveBtn("ads")}>My ads</span>
        <span className={`${activeBtn === 'reviews' ? 'active_btn_blue' : ''}`} onClick={(e) => props.setActiveBtn("reviews")}>Reviews</span>
        <span className={`${activeBtn === 'favorites' ? 'active_btn_blue' : ''}`} onClick={(e) => props.setActiveBtn("favorites")}>Favorites</span>
        <span className={`${activeBtn === 'prices' ? 'active_btn_blue' : ''}`} onClick={(e) => props.setActiveBtn("prices")}>Prices</span>
        <span className={`${activeBtn === 'adbalances' ? 'active_btn_blue' : ''}`} onClick={(e) => props.setActiveBtn("balance")}>Balance</span>
        <span className={`${activeBtn === 'customize' ? 'active_btn_blue' : ''}`} onClick={(e) => props.setActiveBtn("customize")}>Customize</span>
        <span className={`${activeBtn === 'support' ? 'active_btn_blue' : ''}`} onClick={(e) => props.setActiveBtn("support")}>
          Technical support
        </span>
      </div>
    </div>
  );
}
