import { GET_ALL_SUPPORT_TASKS, DELETE_SUPPORT_TASK } from "./types";
import fetch from 'isomorphic-unfetch';
import { authenticationService } from "../../_services";
export const getAllSupportTasks = () => async dispatch => {
  const user = authenticationService.currentUserValue;
  const response = await fetch(`https://intim-vibe-api.padilo.pro/api/admin/support`, {
    method: 'GET',
    headers: {
      'Content-Type': 'application/json',
      'Access-Control-Allow-Origin': '*',
      'Authorization': `${user.token_type} ${user.token}`
    }
  });
  const promise = response.json();
  promise.then(res => {
    return dispatch({
      type: GET_ALL_SUPPORT_TASKS,
      payload: res.data
    });
  }).catch(err => console.error('Error: ', err));
};
export const editSupportTasks = data => async dispatch => {
  const user = authenticationService.currentUserValue;
  const response = await fetch(`https://intim-vibe-api.padilo.pro/api/admin/support/edit/${data.id}`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Access-Control-Allow-Origin': '*',
      'Authorization': `${user.token_type} ${user.token}`
    },
    body: JSON.stringify({
      subject: data.subject,
      message: data.message,
      answer: data.answer
    })
  });
  const promise = response.json();
  promise.then(res => {
    return dispatch({
      type: GET_ALL_SUPPORT_TASKS,
      payload: res
    });
  }).then(() => window.location.reload(true));
};
