import React, { useState, useEffect } from "react";
import s from "./profile.module.scss";

export default function ProfileCarousel({ photo }) {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    setCurrentIndex(0);
  }, []);

  const changePhotoForvard = n => {
    if (photo.length > 1) {
      if (currentIndex < photo.length - 1) {
        setCurrentIndex(currentIndex + 1);
      } else {
        setCurrentIndex(0);
      }
    }
  }
  const changePhotoBack = n => {
    if (photo.length > 1) {
      if (currentIndex > 0) {
        setCurrentIndex(currentIndex - 1);
      } else {
        setCurrentIndex(photo.length - 1);
      }
    }
  }

  return (
    <div className="w-100" style={{ background: '#1b1c1f' }}>
      <span className={s.photo_toggler_back}
        onClick={changePhotoBack}>&lt;</span>

      <div className={s.slide}>
        <img src={photo[currentIndex]}
          alt="model" className="mx-auto" />
      </div>

      <span className={s.photo_toggler_forvard}
        onClick={changePhotoForvard}>&gt;</span>
    </div>
  );
}
