import { authHeader, handleResponse } from '../_helpers';
export var userService = {
  getAll: getAll,
  getById: getById,
  update: update,
  deleteUser: deleteUser
};

function getAll() {
  var requestOptions = {
    method: 'GET',
    headers: authHeader()
  };
  return fetch("/users", requestOptions).then(handleResponse);
}

function getById(id) {
  var requestOptions = {
    method: 'GET',
    headers: authHeader()
  };
  return fetch("/users/".concat(id), requestOptions).then(handleResponse);
}

function update(id, name, second_name, email, phone, rating, position, telegram, viber, whatsapp, site, avatar) {
  var requestOptions = {
    method: 'POST',
    headers: authHeader(),
    body: JSON.stringify({
      name: name,
      second_name: second_name,
      email: email,
      phone: phone,
      rating: rating,
      position: position,
      telegram: telegram,
      viber: viber,
      whatsapp: whatsapp,
      site: site,
      avatar: avatar
    })
  };
  return fetch("/api/auth/user/update/".concat(id), requestOptions).then(handleResponse);
}

function deleteUser(id) {
  var requestOptions = {
    method: 'DELETE',
    headers: authHeader()
  };
  return fetch("/api/auth/user/destroy/".concat(id), requestOptions).then(handleResponse);
}
