import { SELECT_STATE, SELECT_CITY, SELECT_LIST_SORT, SELECT_LIST_FILTER, SELECT_CATEGORY } from './types'; // import fetch from 'isomorphic-unfetch';

export function selectState(state) {
  return {
    type: SELECT_STATE,
    payload: state
  };
}
export function selectCity(city) {
  return {
    type: SELECT_CITY,
    payload: city
  };
}
export function selectListSort(sort) {
  return {
    type: SELECT_LIST_SORT,
    payload: sort
  };
}
export function selectListFilter(filter) {
  return {
    type: SELECT_LIST_FILTER,
    payload: filter
  };
}
export function selectCategory(category) {
  return {
    type: SELECT_CATEGORY,
    payload: category
  };
}
