  id: number | string,
  name: string,
  secondName: string,
  accountingName: string,
  sex: string,
  age: number | string,
  email: string,
  phone: number | string,
  ethnithity: string,
  hair: string,
  eyes: string,
  height: number | string,
  affiliation: string,
  availableTo: string,
  availability: string,
  shortSummary: string,
  about: string,
  photos: string[],
  videos: string[]
}

// user
export const SELECT_STATE = 'USER/SELECT_STATE';
