import React, { useState } from 'react';
import s from './login.module.scss';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { useRouter } from 'next/router';
import { authenticationService } from '../../_services';

export default function SignIn() {
  const [formUser, setFormUser] = useState(true);
  const router = useRouter();

  return (
    <div className={s.login_page_form}>
      <div className="alert alert-info">
        <strong>Normal User</strong> - U: user P: user<br />
        <strong>Administrator</strong> - U: admin P: admin<br />
        <strong>Provider</strong> - U: pro P: pro
        </div>
      <h2>Login</h2>
      <Formik
        initialValues={{
          email: '',
          password: ''
        }}
        validationSchema={Yup.object().shape({
          email: Yup.string().required('email is required'),
          password: Yup.string().required('Password is required')
        })}
        onSubmit={({ email, password }, { setStatus, setSubmitting }) => {
          setStatus();
          authenticationService.login(email, password)
            .then(
              user => {
                const { from } =
                  // this.props.location.state ||
                  { from: { pathname: "/" } };
                // router.push(from);
              },
              error => {
                setSubmitting(false);
                setStatus(error);
              }
            );
        }}
        render={({ errors, status, touched, isSubmitting }) => (
          <Form>
            <div className={`${s.role_toggler}`}>
              {formUser ? <span className="text-info pr-3">User</span> : <span className="text-danger pr-3">Model</span>}
              <label className={s.switch} htmlFor="checkbox">
                <input type="checkbox" id="checkbox" onChange={() => setFormUser(!formUser)} />
                <div className={`${s.slider} ${s.round}`} />
              </label>
            </div>

            <div className="form-group">
              <label htmlFor="email">Email</label>
              <Field name="email" type="text" className={'form-control' + (errors.email && touched.email ? ' is-invalid' : '')} />
              <ErrorMessage name="email" component="div" className="invalid-feedback" />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <Field name="password" type="password" className={'form-control' + (errors.password && touched.password ? ' is-invalid' : '')} />
              <ErrorMessage name="password" component="div" className="invalid-feedback" />
            </div>
            <div className="form-group">
              <button type="submit" className={`btn ${s.submit_btn}`} disabled={isSubmitting}>Submit</button>
              {isSubmitting &&
                <div className="spinner-border spinner-border-sm text-primary ml-3" role="status">
                  <span className="sr-only">Loading...</span>
                </div>
              }
            </div>
            {status &&
              <div className={'alert alert-danger'}>{status}</div>
            }
          </Form>
        )}
      />
    </div>
  )
}
