import React, { useEffect } from 'react'
import AddPostForm from '../components/add-post/AddPostForm'
import { connect } from 'react-redux'
import { addProviderPost } from '../redux/actions/providerActions'
import { getAllPosts, getLocationsInfo } from '../redux/actions/actions'

function AddPost({
  addProviderPost,
  getLocationsInfo,
  posts,
  getAllPosts,
  affiliation,
  availability,
  available_to,
  ethnicity,
  eyes,
  hair,
  states,
  cities
}) {

  useEffect(() => {
    getAllPosts();
    getLocationsInfo();
  }, []);

  return (
    <div>
      <div className="h3 text-white py-2 text-center">{posts && posts.length ? posts.length : 0} posts</div>
      <AddPostForm
        addProviderPost={addProviderPost}
        affiliation={affiliation}
        availability={availability}
        available_to={available_to}
        ethnicity={ethnicity}
        eyes={eyes}
        hair={hair}
        states={states}
        cities={cities}
      />
    </div>
  )
}

const mapStateToProps = state => {
  return {
    affiliation: state.app.affiliation,
    availability: state.app.availability,
    available_to: state.app.available_to,
    ethnicity: state.app.ethnicity,
    eyes: state.app.eyes,
    hair: state.app.hair,
    posts: state.app.posts,
    states: state.app.states,
    cities: state.app.cities
  }
}

const mapDispatchToProps = {
  addProviderPost, getAllPosts, getLocationsInfo
}


export default connect(mapStateToProps, mapDispatchToProps)(AddPost);
