import React, { useState, useEffect } from "react";
import s from "./control.module.scss";

export default function LocPageControl() {
  const [cities, setCities] = useState([
    'South', 'Corning',
    'Cayuga Heights',
    'Fairport',
    'Orchard Park',
    'Pittsford',
    'Williamsville',
    'Tonawanda',
    'Youngstown',
    'Plandome',
    'East Aurora',
    'East Ithaca',
    'Harris Hill',
    'Skaneateles',
    'Manlius',
    'West Seneca',
    'Dering Harbor',
    'Flower Hill',
    'Billington Heights',
    'Chappaqua',
    'Rye',
    'Fayetteville',
    'SUNY Oswego',
    'Chadwicks',
    'Hamburg']);
  const [currentCity, setCurrentCity] = useState('Fairport');

  useEffect(() => {
    setCurrentCity('Fairport');
  }, []);

  const changeCity = city => {
    for (let btn of $('.city-item')) {
      if ($(btn).not($(e.target)).hasClass('active_btn_blue')) {
        $(btn).removeClass('active_btn_blue');
      }
    }
    $(e.target).addClass('active_btn_blue');
  }

  return (
    <div className={`${s.loc_page_control} text-white`}>
      <div className={s.loc_page_control_content}>
        <div className={`${s.loc_page_control_header}`}>
          <h5>ESCORTS IN LOS ANGELES</h5>
          <div>CHANGE LOCATION</div>
        </div>
        <div className={`${s.loc_page_filter_control}`}>
          <div>FILTER</div>
          <input type="text" placeholder="filter"
            className="form-control text-white" />
        </div>
      </div>

      <div className={s.loc_cities_list}>
        <ul>{cities.map((c, i) => (
          <li onClick={e => changeCity(c)} key={i} className={`${currentCity === c ? 'active_btn_blue' : ''} btn city-item`}><span>{c}</span></li>
        ))}</ul>
      </div>
    </div>
  );
}
