import React, { useState } from 'react';
import Link from 'next/link';
import s from "./header.module.scss";
import $ from 'jquery';

const NavComponent = props => {
  const [isOpen, setIsOpen] = useState(true);
  const [activeLink, setActiveLink] = useState();

  const closeNav = () => {
    $('.nav').slideToggle();
  }

  return (
    <div className={s.navbar}>
      <label className={s.navbar_toggler}
        onClick={closeNav}>
        <input type="checkbox" hidden />
        <span></span><span></span><span></span>
      </label>

      <nav className="nav">
        <ul>
          <li>
            <Link href="/modelCabinet"><a
              onClick={() => setActiveLink('model cabinet')} className={activeLink === 'model cabinet' ? s.activeLink : null}>
              Model Cabinet</a></Link>
          </li>
          <li>
            <Link href="/userCabinet"><a
              onClick={() => setActiveLink('user cabinet')} className={activeLink === 'user cabinet' ? s.activeLink : null}>
              User Cabinet</a></Link>
          </li>
          <li>
            <Link href="/addPost"><a
              onClick={() => setActiveLink('add post')} className={activeLink === 'add post' ? s.activeLink : null}>
              Add Post</a></Link>
          </li>
        </ul>
      </nav>
    </div>
  )
}

NavComponent.getInitialProps = async ({ Component, ctx }) => {
  return {}
}

export default NavComponent;
