import { SHOW_LOADER, HIDE_LOADER, SHOW_ALERT, HIDE_ALERT, GET_CATEGORIES, GET_ALL_POSTS, GET_LOCAITONS_INFO, GET_CITIES, CREATE_SUPPORT_TASK, GET_ALL_USER_TASKS } from "../actions/types";

const initialState = {
  loading: false,
  alert: null,
  categories: [],
  posts: [],
  states: [],
  cities: [],
  supportTasks: [
    { theme: 'Task 1 Theme', text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Quis ipsum suspendisse ultrices gravida. Risus commodo viverra maecenas accumsan lacus vel facilisis.', id: 1 },
    { theme: 'Task 2 Theme', text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Quis ipsum suspendisse ultrices gravida. Risus commodo viverra maecenas accumsan lacus vel facilisis.', id: 2 },
    { theme: 'Task 3 Theme', text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Quis ipsum suspendisse ultrices gravida. Risus commodo viverra maecenas accumsan lacus vel facilisis.', id: 3 }
  ]
};

export const appReducer = (state: any = initialState, action: any) => {
  switch (action.type) {
    case SHOW_LOADER:
      return { ...state, loading: true }

    case HIDE_LOADER:
      return { ...state, loading: false }

    case SHOW_ALERT:
      return { ...state, alert: action.payload }

    case HIDE_ALERT:
      return { ...state, alert: null }

    case GET_CATEGORIES:
      return { ...state, categories: action.payload }

    case GET_ALL_POSTS:
      return { ...state, posts: action.payload }

    case GET_LOCAITONS_INFO:
      return { ...state, states: action.payload }

    case GET_CITIES:
      return { ...state, cities: action.payload }

    case CREATE_SUPPORT_TASK:
      return { ...state, supportTasks: [...state.supportTasks, action.payload] }

    case GET_ALL_USER_TASKS:
      return { ...state, supportTasks: action.payload }

    default: return state;
  }
}
