  const requestOptions = {
    method: 'GET',
    headers: { 'Content-Type': 'application/json' }
  };

  return fetch(`/register/activate_email/${token}`, requestOptions)
    .then(handleResponse)
}

function confirmSMS(token: string): any {
  const requestOptions = {
    method: 'GET',
