import React, { useEffect } from 'react'
import AddPostForm from '../components/add-post/AddPostForm'
import { connect } from 'react-redux'
import { addProviderPost } from '../redux/actions/providerActions'
import { getAllPosts } from '../redux/actions/actions'

function AddPost({ addProviderPost, posts, getAllPosts }) {

  useEffect(() => {
    getAllPosts();
  }, []);

  return (
    <div>
      <div className="h3 text-white py-2 text-center">{posts && posts.length ? posts.length : 0} posts</div>
      <AddPostForm addProviderPost={addProviderPost} />
    </div>
  )
}

const mapStateToProps = state => {
  console.log(state);

  return {
    posts: state.app.posts
  }
}

const mapDispatchToProps = {
  addProviderPost, getAllPosts
}


export default connect(mapStateToProps, mapDispatchToProps)(AddPost);
