import React from "react";
import Link from 'next/link';
import Slider from "react-slick";
import s from "./horizontal.module.scss";
import fav from "../../assets/images/main/signs/favorite-yel.png";
import crown from "../../assets/images/main/signs/crown-yel.png";
import star from "../../assets/images/main/signs/star-yel.png";
import plane from "../../assets/images/main/signs/plane-yel.png";

export default function LocHorCarousel({ posts, addToFavorites }) {
  const settings = {
    infinite: true,
    speed: 1000,
    slidesToShow: 2,
    slidesToScroll: 2,
    arrows: true,
    dots: false,
    autoplay: true,
    autoplaySpeed: 8000,
    draggable: false,
    responsive: [{
      breakpoint: 769,
      settings: {
        slidesToShow: 1,
        slidesToScroll: 1,
        initialSlide: 1
      }
    }]
  };

  return (
    <div className={s.loc_hor_carousel}>
      <div className={s.loc_hor_carousel_header}>
        <h5 className="text-white">Paid Announcements</h5>
      </div>
      <Slider
        {...settings}
        style={{
          maxWidth: "1260px",
          margin: "0 auto",
        }} >
        {posts.map((post, i) => {
          return <div key={i}>
            <Link href={{ pathname: "/modelProfile", query: { id: post.id } }}><a>
              <div className={s.loc_hor_carousel_slide}>
                <img src={post.photo_horisontal} alt="model" className="w-100" />
                <div className={s.loc_slide_info}>
                  <div className={s.loc_slide_info_head}>
                    <h5 className="text-white">{post.model_name} {post.second_name}</h5>
                    <div className={s.slide_network}>{post.status}</div>
                    <div className={s.slide_control}>
                      <img src={fav} alt="" onClick={(e) => {
                        e.preventDefault();
                        addToFavorites(post.id);
                      }} />
                      <img src={crown} alt="" />
                      <img src={star} alt="" />
                      <img src={plane} alt="" />
                    </div>
                  </div>
                  <div className={s.slide_location}>
                    <p className="m-0">{post.state.name} / {post.city.name}</p>
                  </div>
                  <div className={s.slide_verification}>
                    <span className={s.verification}>{post.is_verify ? 'OTHER VIP VERIFIED' : null}</span>
                    <span className={`text-white ${s.status}`}>SPECIAL STATUS</span>
                  </div>
                </div>
              </div>
            </a></Link>
          </div>
        })}
      </Slider>
    </div>
  );
}
