import { CREATE_POST, PostsActionTypes, FETCH_POST } from "../actions/types";

const initialState = {
  posts: [],
  fetchedPosts: []
};

export const postsReducer = (state = initialState, action: PostsActionTypes) => {
  console.log(action);
  
  switch (action.type) {
    case CREATE_POST:
      return { ...state, posts: state.posts.concat(action.payload) }

    case FETCH_POST:
      return { ...state, fetchedPosts: action.payload }

    default:
      return state;
  }
}
