import React, { useState } from "react";
import s from "./user-edit.module.scss";
import EscortList from "../home-page/EscortList";
import m3h from "../../assets/images/main/horizontal/model-3.png";
import m4h from "../../assets/images/main/horizontal/model-4.png";
import m1h from "../../assets/images/main/horizontal/model-1.png";
import m2h from "../../assets/images/main/horizontal/model-2.png";

export default function UserCabinetEdit() {
  const [accountEditMode, setAccountEditMode] = useState(false);

  const nameInput = (
    <input
      className="form-control bg-dark text-white"
      type="text"
      placeholder="Name"
    />
  );
  const secondNameInput = (
    <input
      className="form-control bg-dark text-white"
      type="text"
      placeholder="Second Name"
    />
  );

  return (
    <div className={`text-white ${s.cabinet_wrap}`}>
      <div className={s.favorites_list_block}>
        <h5 className={s.favorites_list_block_heading}>YOUR FAVORITES</h5>
        <EscortList models={[m1h, m2h, m3h, m4h, m1h, m2h]} />
      </div>

      <div className={s.customize_block}>
        <div className={s.customize_block_title}>ACCOUNT</div>
        <div className={s.customize_block_info}>
          <div className={s.customize_block_info_text}>
            <dl>
              <dt>Name:</dt>
              <dd>{!accountEditMode ? "Name" : nameInput}</dd>
              <dt>Second Name:</dt>
              <dd>{!accountEditMode ? "Second Name" : secondNameInput}</dd>
            </dl>
          </div>
          <div>
            <span
              className={s.btn_edit}
              onClick={() => {
                setAccountEditMode(!accountEditMode);
              }}
            >
              {!accountEditMode ? "EDIT" : "SAVE"}
            </span>
          </div>
        </div>
      </div>

      <div className={s.reports_block}>
        <h5 className={s.reports_block_heading}>REPORTS YOU'VE LEFT</h5>
        <ul>
          {[1, 2, 3].map((rep, i) => (
            <li ley={i}>
              <div className={s.report}>
                <div className={s.report_title}>
                  <span>MODEL NAME</span>
                  <span>22.07.19</span>
                </div>
                <p>
                  Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed
                  do eiusmod tempor incididunt ut labore et dolore magna aliqua.
								</p>
                <span className={s.add_btn}>TO AD</span>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
