import App from 'next/app';
import { Provider, useDispatch } from 'react-redux';
import React from 'react';
import './styles/app.scss';
import Layout from '../components/layout/Layout';
import withRedux from "next-redux-wrapper";
import 'bootstrap/dist/css/bootstrap.min.css';
import store from '../redux/store';
import Head from 'next/head';

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { getAppInfo } from '../redux/actions/actions';


class CustomApp extends App {
  constructor(props) {
    super(props);
    this.dispatch = useDispatch();
  }

  static async getInitialProps(props) {
    const pageProps = props.Component.getInitialProps ? await props.Component.getInitialProps(props.ctx) : {};
    return {
      pageProps: pageProps
    };
  }

  componentDidMount() {
    this.dispatch(getAppInfo);
  }

  render() {
    const { Component, pageProps, store } = this.props;

    return (
      <Provider store={store}>
        <Head>
          <meta charset='utf-8' />
          <meta http-equiv='X-UA-Compatible' content='IE=edge' />
          <meta name='viewport' content='width=device-width,initial-scale=1,minimum-scale=1,maximum-scale=1,user-scalable=no' />
          <meta name='description' content='Description' />
          <meta name='keywords' content='Keywords' />
          <title>Next.js PWA Example</title>

          <link rel="manifest" href="/manifest.json" />
          <link href='/favicon-16x16.png' rel='icon' type='image/png' sizes='16x16' />
          <link href='/favicon-32x32.png' rel='icon' type='image/png' sizes='32x32' />
          <link rel="apple-touch-icon" href="/apple-icon.png"></link>
          <meta name="theme-color" content="#317EFB" />
        </Head>
        <Layout>
          <Component {...pageProps} />
        </Layout>
      </Provider>
    );
  }
}

//makeStore function that returns a new store for every request
const makeStore = () => store;

//withRedux wrapper that passes the store to the App Component
export default withRedux(makeStore)(CustomApp);
