import { ADD_POST, ProviderPost, UPDATE_POST, REMOVE_POST, GET_ALL_POSTS } from "./types"
import { NextApiRequest, NextApiResponse } from 'next'
import fetch from 'isomorphic-unfetch'
import Cors from 'cors'

// Initializing the cors middleware
const cors = Cors({
  methods: ['GET', 'HEAD'],
})

function runMiddleware(req: NextApiRequest, res:NextApiResponse, fn: any) {
  return new Promise((resolve, reject) => {
    fn(req, res, (result: any) => {
      if (result instanceof Error) {
        return reject(result)
      }

      return resolve(result)
    })
  })
}

export async function handler(req: NextApiRequest, res:NextApiResponse) {
  // Run the middleware
  await runMiddleware(req, res, cors)

  // Rest of the API logic
  res.json({ message: 'Hello Everyone!' })
}

export function getAllProviderPost(): any {
  return {
    type: GET_ALL_POSTS
  }
};
export async function addProviderPost(post: ProviderPost): Promise<any> {
  await fetch('https://intim-vibe-api.padilo.pro/api/posts/create', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Access-Control-Allow-Origin': '*'
    },
    body: JSON.stringify({
      gender: 'female',
      age: 25,
      ethnicity: 'some',
      hair_color: 'some',
      eye_color: 'some',
      height: 'some',
      affiliation: 'some',
      number: '+0123456789',
      email: 'some@mail.com',
      model_name: 'some',
      video_link: 'some',
      about: 'some',
      available_to: ['some', 'some'],
      availability: ['some', 'some'],
      summary: 'some',
      second_name: 'some',
      account_name: 'some',
      category: 'some',
      //         не обязательные поля:
      status: 'other'
    })
  })
    .then(res => console.log(res))
    .catch(err => console.log('Error:', err))

  return {
    type: ADD_POST,
    payload: post
  }
};
export function updateProviderPost(post: ProviderPost): any {
  return {
    type: UPDATE_POST,
    payload: post
  }
};
export function removeProviderPost(id: number | string): any {
  return {
    type: REMOVE_POST,
    payload: id
  }
};

